import React, { useState } from "react";
import * as employeeService from "../services/EmployeeService";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
const AddEmployeeComponent = () => {
  const [empId, setEmpId] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [everestEmailId, setEverestEmailId] = useState("");
  const [password, setPassword] = useState("");
  const [personalEmailId, setPersonalEmailId] = useState("");
  const[addressLine1,setAddressLine1]=useState("")
  const[addressLine2,setAddressLine2]=useState("")
  const[city,setCity]=useState("")
  const[state,setState]=useState("")
 const[zipCode,setZipCode]=useState("")
 const[id]=useState("")
 const[presentAddress,setPresentAddress]=useState({
  id,
  addressLine1,
  addressLine2,
  city,
  state,
  zipCode
 })


 const[permanentAddress,setPermanentAddress]=useState({
  id,
  addressLine1,
  addressLine2,
  city,
  state,
  zipCode
 })
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!firstName || !everestEmailId || !password) {
      alert("Enter all fields");
      return;
    }
    const employee = {
      firstName,
      lastName,
      everestEmailId,
      password,
      personalEmailId,
      presentAddress,
      permanentAddress 

    };

    employeeService
      .createEmployee(employee)
      .then((response) => {
        console.log("employee saved successfully", response);
        setFirstName("");
        setLastName("");
        setEverestEmailId("");
        setPassword("");
        setPersonalEmailId("");
        setPresentAddress("");
        setPresentAddress().setId("");
        setPresentAddress("").setAddressLine1("");
        setPresentAddress("").setAddressLine2("");
        setPresentAddress("").setCity("");
        setPresentAddress("").setState("");
        setPresentAddress("").setZipCode("");
        setPermanentAddress("");
       setPermanentAddress("").setId("");
       setPermanentAddress("").setAddressLine1("");
       setPermanentAddress("").setAddressLine2("");
       setPermanentAddress("").setCity("");
       setPermanentAddress("").setState("");
        setPermanentAddress("").setZipCode("");
      })
      .catch((e) => console.log("error", e));
  };

  return (
    <div className="container">
      <h1>Employee Portal</h1>
      <hr />
      <Link className="btn btn-primary" to="/">
        back to Home
      </Link>
      <div id={"addEmployeeForm"}>
        <h2>Add New Employee</h2>
        <form
          onSubmit={(e) => handleSubmit(e)}
          className="row justify-content-center"
        >
          <div className="form-group col-md-5">
            <label htmlFor="firstName">FirstName</label>
            <input
              id="firstName"
              placeholder={"Enter the firstName"}
              className="form-control col-md-12"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="lastName">LastName</label>
            <input
              id="lastName"
              placeholder={"Enter the lastName"}
              className="form-control col-md-12"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="email">Everest Email</label>
            <input
              id="email"
              placeholder={"Enter the email"}
              className="form-control col-md-12"
              type="text"
              value={everestEmailId}
              onChange={(e) => setEverestEmailId(e.target.value)}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              placeholder={"Enter the password"}
              className="form-control col-md-12"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="form-group col-md-10">
            <label htmlFor="email">Personal Email</label>
            <input
              id="email"
              placeholder={"Enter the email"}
              className="form-control col-md-12"
              type="text"
              value={personalEmailId}
              onChange={(e) => setPersonalEmailId(e.target.value)}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="addressLine1">Address Line1</label>
            <input
              id="addressLine1"
              placeholder={"Enter the AddressLine1"}            
                className="form-control col-md-12"

              onChange={(e) =>setPresentAddress({addressLine1:e.target.value})}

            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="addressLine2">Address Line1</label>
            <input
              id="addressLine2"
              placeholder={"Enter the AddressLine2"}
              className="form-control col-md-12"

              onChange={(e) => setPresentAddress({addressLine2:e.target.value})}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="city">City</label>
            <input
              id="city"
              placeholder={"Enter the city"}
              className="form-control col-md-12"
              type="text"

              onChange={(e) => setPresentAddress({city:e.target.value})}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="state">State</label>
            <input
              id="state"
              placeholder={"Enter the state"}
              className="form-control col-md-12"


              onChange={(e) =>setPresentAddress({state:e.target.value})}
            />
          </div>
          <div className="form-group col-md-10">
            <label htmlFor="zipCode">Zip Code</label>
            <input
              id="zipCode"
              placeholder={"Enter the zipCode"}
              className="form-control col-md-12"
              type="text"

               onChange={(e) => setPresentAddress({zipCode:e.target.value})}
            />
            setPresentAddress
            </div>
            <div className="form-group col-md-5">
            <label htmlFor="addressLine1">Address Line1</label>
            <input
              id="addressLine1"
              placeholder={"Enter the AddressLine1"}
              className="form-control col-md-12"

              onChange={(e) =>setPermanentAddress({addressLine1:e.target.value})}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="addressLine2">Address Line2</label>
            <input
              id="addressLine2"
              placeholder={"Enter the AddressLine2"}
              className="form-control col-md-12"

              onChange={(e) => setPermanentAddress({addressLine2:e.target.value})}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="city">City</label>
            <input
              id="city"
              placeholder={"Enter the city"}
              className="form-control col-md-12"
              type="text"

              onChange={(e) => setPermanentAddress({city:e.target.value})}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="state">State</label>
            <input
              id="state"
              placeholder={"Enter the state"}
              className="form-control col-md-12"


              onChange={(e) => setPermanentAddress({state:e.target.value})}
            />
          </div>
          <div className="form-group col-md-10">
            <label htmlFor="zipCode">Zip Code</label>
            <input
              id="zipCode"
