import React, { useEffect, useState } from "react";
import { useHistory, useParams,Link } from "react-router-dom";

import * as employeeService from "../services/EmployeeService";
import "bootstrap/dist/css/bootstrap.min.css";
const UpdateEmployeeComponent = () => {
  const { id } = useParams();

  const [employee, setEmployee] = useState({
    firstName: "",
    lastName: "",
    everestEmailId: "",
    password: "",
    personalEmailId: "",
    presentAddress:"",
    permanentAddress:""
  });
  const[presentAddress,setPresentAddress]=useState({
    id:'',
    addressLine1:'',
    addressLine2:'',
    city:'',
    state:'',
    zipCode:''
   })


   const[permanentAddress,setPermanentAddress]=useState({
    id:'',
    addressLine1:'',
    addressLine2:'',
    city:'',
    state:'',
    zipCode:''
   })

  useEffect(() => {
    loadUser();
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    employeeService
      .updateEmployee(id, employee)
      .then((response) => {
        console.log("employee saved successfully", response);
        setFirstName("");
        setLastName("");
        setEverestEmailId("");
        setPassword("");
        setPersonalEmailId("");

      })
      .catch((e) => console.log("error", e));
  };

  const loadUser = async () => {
    employeeService.getEmployeeById(id).then((response) => {
      setFirstName(response.data.firstName);
      setLastName(response.data.lastName);
      setEverestEmailId(response.data.everestEmailId);
      setPassword(response.data.password);
      setPersonalEmailId(response.data.personalEmailId);
      setEmployee(response.data);
      setPresentAddress(response.data.presentAddress).setAddressLine1(response.data.presentAddress.addressLine1);
      setPresentAddress(response.data.presentAddress).setAddressLine2(response.data.presentAddress.addressLine2);
      setPresentAddress(response.data.presentAddress).setCity(response.data.presentAddress.pcity);
      setPresentAddress(response.data.presentAddress).setState(response.data.presentAddress.state);
      setPresentAddress(response.data.presentAddress).setZipCode(response.data.presentAddress.zipCode);
      setPermanentAddress(response.data.permanentAddress).setAddressLine1(response.data.permanentAddress.addressLine1);
      setPermanentAddress(response.data.permanentAddress).setAddressLine2(response.data.permanentAddress.addressLine2);
      setPermanentAddress(response.data.permanentAddress).setCity(response.data.permanentAddress.city);
      setPermanentAddress(response.data.permanentAddress).setState(response.data.permanentAddress.state);
      setPermanentAddress(response.data.permanentAddress).setZipCode(response.data.permanentAddress.zipCode);
      setPermanentAddress(response.data.permanentAddress);
      setPresentAddress(response.data.presentAddress);
      console.log(response.data.per)
    }).catch((e) => console.log("error", e));
  };

  return (
    <div className="container">
      <h1>Employee Portal</h1>
      <hr />
      <Link className="btn btn-primary" to="/">
        back to Home
      </Link>

      <div id={"addEmployeeForm"}>
        <h2>Update Employee</h2>
        <form
          onSubmit={(e) => onSubmit(e)}
          className="row justify-content-center"
        >
          <div className="form-group col-md-5">
            <label htmlFor="firstName">FirstName</label>
            <input
              id="firstName"
              placeholder={"Enter the firstName"}
              className="form-control col-md-12"
              defaultValue={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="lastName">LastName</label>
            <input
              id="lastName"
              placeholder={"Enter the lastName"}
              className="form-control col-md-12"
              defaultValue={employee.lastName}
              onChange={(e) => setLastName(e.target.value)}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="email">Everest Email</label>
            <input
              id="email"
              placeholder={"Enter the email"}
              className="form-control col-md-12"
              type="text"
              defaultValue={employee.everestEmailId}
              onChange={(e) => setEverestEmailId(e.target.value)}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              placeholder={"Enter the password"}
              className="form-control col-md-12"
              type="password"
              defaultValue={employee.password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="form-group col-md-10">
            <label htmlFor="email">Personal Email</label>
            <input
              id="email"
              placeholder={"Enter the email"}
              className="form-control col-md-12"
              type="text"
              defaultValue={employee.personalEmailId}
              onChange={(e) => setPersonalEmailId(e.target.value)}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="addressLine1">Address Line1</label>
            <input
              id="addressLine1"
              placeholder={"Enter the AddressLine1"}            
                className="form-control col-md-12"
            type="text"
            value={presentAddress.addressLine1}
              onChange={(e) =>setPresentAddress({...presentAddress,addressLine1:e.target.value})}

            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="addressLine2">Address Line2</label>
            <input
              id="addressLine2"
              placeholder={"Enter the AddressLine2"}
              className="form-control col-md-12"
             type="text"
             value={presentAddress.addressLine2}
              onChange={(e) => setPresentAddress({...presentAddress,addressLine2:e.target.value})}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="city">City</label>
            <input
              id="city"
              placeholder={"Enter the city"}
              className="form-control col-md-12"
              type="text"
              value={presentAddress.city}

              onChange={(e) => setPresentAddress({...presentAddress,city:e.target.value})}
            />
          </div>
          <div className="form-group col-md-5">
            <label htmlFor="state">State</label>
            <input
              id="state"
              placeholder={"Enter the state"}
              className="form-control col-md-12"
